mapper.select:
select 

from t_account_info
where userId = #{userid,jdbcType=VARCHAR}

      and businessId = #{businessid,jdbcType=INTEGER}

select
userId, businessId, remainder, totality, remark
from t_account_info
where 1=1

userId = #{_parameter,jdbcType=VARCHAR}

limit  #{start,jdbcType=INTEGER} ,#{num,jdbcType=INTEGER}

mapper.delete:
delete from t_account_info
where userId = #{userid,jdbcType=VARCHAR}
  and businessId = #{businessid,jdbcType=INTEGER}

mapper.update:
update t_account_info


    remainder = #{remainder,jdbcType=INTEGER},


    totality = #{totality,jdbcType=INTEGER},


    remark = #{remark,jdbcType=VARCHAR},


where userId = #{userid,jdbcType=VARCHAR}
  and businessId = #{businessid,jdbcType=INTEGER}

update t_account_info
set remainder = #{remainder,jdbcType=INTEGER},
  totality = #{totality,jdbcType=INTEGER},
  remark = #{remark,jdbcType=VARCHAR}
where userId = #{userid,jdbcType=VARCHAR}
  and businessId = #{businessid,jdbcType=INTEGER}
