mapper.select:
select 

from t_spending_detail
where yearmonth = #{yearmonth,jdbcType=CHAR}
  and userId = #{userid,jdbcType=VARCHAR}

mapper.delete:
delete from t_spending_detail
where yearmonth = #{yearmonth,jdbcType=CHAR}
  and userId = #{userid,jdbcType=VARCHAR}

mapper.insert:
insert into t_spending_detail (yearmonth, userId, amount, 
  lastbalance, recharge, remark
  )
values (#{yearmonth,jdbcType=CHAR}, #{userid,jdbcType=VARCHAR}, #{amount,jdbcType=DECIMAL}, 
  #{lastbalance,jdbcType=DECIMAL}, #{recharge,jdbcType=DECIMAL}, #{remark,jdbcType=VARCHAR}
  )

mapper.update:
update t_spending_detail
set amount = #{amount,jdbcType=DECIMAL},
  lastbalance = #{lastbalance,jdbcType=DECIMAL},
  recharge = #{recharge,jdbcType=DECIMAL},
  remark = #{remark,jdbcType=VARCHAR}
where yearmonth = #{yearmonth,jdbcType=CHAR}
  and userId = #{userid,jdbcType=VARCHAR}
