mapper.select:
SELECT MAX(userId) userId,MAX(userName) userName,assignorId,MAX(assignorName) assignorName,yearMonth,max(remark) remark, MAX(CASE businessId WHEN 1 THEN assignQuantity ELSE 0 END) b1q , MAX(CASE businessId WHEN 1 THEN usedQuantity ELSE 0 END) b1uq , MAX(CASE businessId WHEN 2 THEN assignQuantity ELSE 0 END) b2q , MAX(CASE businessId WHEN 2 THEN usedQuantity ELSE 0 END) b2uq FROM ( SELECT u.userId,u.userName,a.assignorId, (SELECT username FROM t_user WHERE userId = a.assignorId) assignorName, a.yearMonth,a.businessId,a.assignQuantity,a.usedQuantity,a.remark FROM t_user u,t_user_apply_package a WHERE u.userId=a.userId
AND a.userId=#{userId,jdbcType=VARCHAR}
AND u.userName like '%${userName}%'
AND yearMonth >=#{yearMonth,jdbcType=VARCHAR}
) xx GROUP BY assignorId,yearMonth ORDER BY yearMonth DESC

SELECT userId,MAX(userName) userName,assignorId,MAX(assignorName) assignorName,yearMonth,max(remark) remark, MAX(CASE businessId WHEN 1 THEN assignQuantity ELSE 0 END) b1q , MAX(CASE businessId WHEN 1 THEN usedQuantity ELSE 0 END) b1uq , MAX(CASE businessId WHEN 2 THEN assignQuantity ELSE 0 END) b2q , MAX(CASE businessId WHEN 2 THEN usedQuantity ELSE 0 END) b2uq FROM ( SELECT u.userId,u.userName,a.assignorId, (SELECT username FROM t_user WHERE userId = a.assignorId) assignorName, a.yearMonth,a.businessId,a.assignQuantity,a.usedQuantity,a.remark FROM t_user_apply_package a,t_user u WHERE u.userId=a.userId
AND a.userId=#{userId,jdbcType=VARCHAR}
AND u.userName like '%${userName}%'
AND yearMonth =#{yearMonth,jdbcType=VARCHAR}
) xx GROUP BY userId,assignorId,yearMonth ORDER BY yearMonth DESC
limit #{cursor,jdbcType=INTEGER} ,#{length,jdbcType=INTEGER}

select count(*) from ( SELECT MAX(userId),MAX(userName) userName,assignorId,MAX(assignorName) assignorName,yearMonth,max(remark) remark, MAX(CASE businessId WHEN 1 THEN assignQuantity ELSE 0 END) b1q , MAX(CASE businessId WHEN 1 THEN usedQuantity ELSE 0 END) b1uq , MAX(CASE businessId WHEN 2 THEN assignQuantity ELSE 0 END) b2q , MAX(CASE businessId WHEN 2 THEN usedQuantity ELSE 0 END) b2uq FROM ( SELECT u.userId,u.userName,a.assignorId, (SELECT username FROM t_user WHERE userId = a.assignorId) assignorName, a.yearMonth,a.businessId,a.assignQuantity,a.usedQuantity,a.remark FROM t_user u,t_user_apply_package a WHERE u.userId=a.userId
AND a.userId=#{userId,jdbcType=VARCHAR}
AND u.userName like '%${userName}%'
AND yearMonth =#{yearMonth,jdbcType=VARCHAR}
) xx GROUP BY userId,assignorId,yearMonth ) yyy

select 
from t_user_apply_package
where userId=#{userId,jdbcType=VARCHAR}
and yearMonth=#{yearMonth,jdbcType=VARCHAR}

mapper.update:
update t_user_apply_package
set usedQuantity = usedQuantity + #{quantity,jdbcType=INTEGER}
where userId = #{userId,jdbcType=INTEGER}  and businessId=1 and yearMonth=#{yearMonth,jdbcType=VARCHAR}
